def concat(xs):
    """The concatenation of all the elements in a list.
    """
    return list(chain(*xs))

def nub(xs):
    """A list containing the same elements as xs,
       without duplicates, in the order of their
       first occurrence.
    """
    return list(dict.fromkeys(xs))
